import React, { useState } from "react";
import FAQ from './FAQ';
import './style.css'

export function FaqPage(props) {

    const [faqs, setfaqs] = useState([
        {
            question: 'This is a test question?',
            answer: 'This is a longer test answer hope this works',
            open: false
        },
        {
            question: 'This is a test question? 2',
            answer: 'This is a longer test answer hope this works 2',
            open: false
        }
    ]);

    const toggleFAQ = index => {
        setfaqs(faqs.map((faq, i)))
    }
    

    return (
        <body>
            <div className="faqs">
                {faqs.map((faq, i) => (
                   <FAQ faq={faq} index={i}/> 
                ))}
            </div>
        </body>
    )
}
