import React, { useState } from 'react'
import { Link } from 'react-router-dom'
import { CKEditor } from '@ckeditor/ckeditor5-react'
import ClassicEditor from '@ckeditor/ckeditor5-build-classic'
import axios from 'axios'
import './style.css';
import parse from 'html-react-parser'
import { Checkbox } from '@material-ui/core'

export function StaffPage(props) {
    const [text, setText] = useState('')
    const [checked, setCheck] = useState(false)

    const navSlide = () => {
        const burger = document.querySelector('.burger');
        const nav = document.querySelector('.nav-links')
        const navLinks = document.querySelectorAll('.nav-links a')

        // Toggle Links
        nav.classList.toggle('nav-active')


        //Animate Links
        navLinks.forEach((link, index) => {
            if (link.style.animation) {
                link.style.animation = ''
            } else {
                link.style.animation = `navLinkFade 0.5s ease forwards ${index / 5 + 0.5}s`
            }
        })

        //Burger Animation
        burger.classList.toggle('toggle')
    }

    async function checkHandler() {
        if (checked === true) {
            await setCheck(false)
        } else if (checked === false) {
            await setCheck(true)
        }
    }

    function submitHandler(event) {

        if (text.length > 1) {
           

            axios.get('http://localhost:5001/api/auth')
            .then((res) => {
                const content = {
                    app: text,
                    reqs: checked,
                    id: res.data.discordId
                }

                console.log(res.data)
                /*axios.post('http://localhost:5001/api/staff', content)
                .then(response => {
                    console.log(`Success!`)
                }).catch(err => {
                    console.error(err)
                })*/
            })
            .catch(() => {
                console.log('There was an error getting the number of users.')
            })
        } else {
            return
        }

        //console.log(content)
    }

    return (
        <body>
            <div className="nav">

                <div className="page-cont">
                    <ul className="nav-links">
                        <Link><li><a className="aa" onClick={() => {
                            window.open("https://discord.com/invite/96MAcmwA", "_blank")
                        }}>Discord</a></li></Link>
                        <Link to="/faq"><li><a className="ab" >Faq</a></li></Link>
                        <Link to="/rules"><li><a className="ac" >Rules</a></li></Link>
                        <Link to="/team"><li><a className="ad" >Team</a></li></Link>
                    </ul>

                    <div className="burger" onClick={navSlide}>
                        <div className="line1"></div>
                        <div className="line2"></div>
                        <div className="line3"></div>
                    </div>
                </div>

                <Link to="/dashboard">
                    <button className="login-btn">
                        Dashboard
        <a class="login-btn-logo"><i class="fas fa-address-book"></i></a>
                    </button>
                </Link>
            </div>

            <div className="app-background">
                <div className="editor-container">
                    <h1 className="app-type">Staff Application</h1>
                    <h2 className="app-title" >Requirements</h2>
                    <div className="app-recs-list">
                        <p>- Must be 14 years old</p> <br />
                        <p>- Has been in the server for 2 weeks or more</p> <br />
                        <p>- Wants to help others</p> <br />
                        <p>- Knows how to operate discord like a pro</p> <br />
                        <p>- Knows how to use bots and other tools</p> <br />
                        <p>- Knows our rules well</p> <br />
                        <p>- Has a great english vocabulary</p> <br />
                    </div>
                    <div className="recs-check">
                        <input type="checkbox" onClick={
                            checkHandler
                        }/>
                        <p className="check-tag">I meet and agree to all the requirements</p>
                    </div>
                    <h2 className="app-title" >APPLICATION</h2>
                    <CKEditor
                        className="editor"
                        data={`
                        <p>Discord Name and Identifier :</p><br/>
                        <p>What is your region? (NA, EU, AU) :</p><br/>
                        <p>What Time Zone are you in? :</p><br/>
                        <p>How old are you? (minimum 14) :</p><br/>
                        <p>Why do you want to be a part of our staff team? (60-word minimum):</p><br/>
                        <p>What do you believe the goal of a staff team is? (60-word minimum):</p><br/>
                        <p>What are the three strengths and weaknesses you have? (80-word minimum):</p><br/>
                        <p>Why should we choose you over other applications? (100-word minimum): <br/><br/> </p>`}

                        editor={ClassicEditor}
                        onChange={(even, editor) => {
                            const data = editor.getData()
                            setText(data)
                        }}
                    />
                </div>
                <div className="app-buttons">
                    <Link to="/applications"><button className="cancel-btn">Cancel</button></Link>
                    <button className="apply-btn" onClick={
                        submitHandler
                    }>Apply</button>
                </div>
            </div>

            <div className="footer-container">
                <footer>
                    <div className="container">
                        <div className="about">
                            <h2>About</h2>
                            <p>Simplify Code is a coding community for everyone to join, we are growing more every day so why not join?</p>
                            <br />
