import './style.css'
import React from 'react'
import { getUserDetails } from '../../../utils/api'
import { Link } from '@material-ui/core'
import axios from 'axios'

export function NavLogin() {

    const [loggedIn, setLoggedIn] = React.useState(false)
    const [user, setUser] = React.useState({})
    const [open, setOpen] = React.useState(false)

    async function logout() {
        await axios.post("https://simplify-code.com/api/logout", { id: user.discordId }).then(() => {
            setLoggedIn(false)
        })
    }

    /*React.useEffect(() => {
        getUserDetails()
            .then(({ data }) => {
               setLoggedIn(true)
               setUser(data)
            }).catch(() => {
                setLoggedIn(false)
            })
    }, [])*/

    if (loggedIn) {
        return (
            <div>
                <div className={"navLogin " + (open ? "open" : "")} onClick={() => setOpen(!open)}>
                    <img className="navLogin-avatar" src="https://cdn.discordapp.com/avatars/359681648322805761/b5865a8fcbdfd01dc3017d7a137944a3.webp?size=128" alt="" />
                    <span className="navLogin-username">Zofux</span>
                    <i class="fas fa-caret-down"></i>
                </div>
                <div className={"menu " + (open ? "open" : "")}>
                    <Link to="/dashbaord"><p>Dashboard</p></Link>
                    <Link to="/dashboard/report"><p>Report</p></Link>
                    <p className="logout" onClick={logout}>Logout</p>
                </div>
            </div>
        )
    } else {
        return (
            <div>
                <div className={"navLogin " + (open ? "open" : "")} onClick={() => setOpen(!open)}>
                    <img className="navLogin-avatar" src="https://cdn.discordapp.com/avatars/359681648322805761/b5865a8fcbdfd01dc3017d7a137944a3.webp?size=128" alt="" />
                    <span className="navLogin-username">Zofux</span>
                    <i class="fas fa-caret-down"></i>
                </div>
                <div className={"menu " + (open ? "open" : "")}>
                    <p className="menu-link" onClick={() => window.open("https://github.com/Simplify-Development", "_blank")}>Github</p>
                    <p className="menu-link" onClick={() => window.open("https://discord.gg/XveJX7Z")}>Discord</p>
                    <p className="login" onClick={() => window.location.href = `https://simplify-code.com/api/auth/discord`}>Login</p>
                </div>
            </div>
        )
    }
}
