import React, { useState } from "react";
import FAQ from './FAQ';
import './style.css'

export function FaqPage(props) {

    const [faqs, setfaqs] = useState([
        {
            question: 'How do I become staff?',
            answer: 'To become staff in our server you can apply on this website, we also announce when staff applications open in <',
            open: false
        },
        {
            question: 'This is a test question? 2',
            answer: 'This is a longer test answer hope this works 2',
            open: false
        }
    ]);

    const toggleFAQ = index => {
        setfaqs(faqs.map((faq, i) => {
            if (i === index) {
                faq.open = !faq.open
            } else {
                faq.open = false;
            }

            return faq;
        }))
    }
    

    return (
        <body>
            <div className="faqs">
                {faqs.map((faq, i) => (
                   <FAQ faq={faq} index={i} toggleFAQ={toggleFAQ} /> 
                ))}
            </div>
        </body>
    )
}
