import React from "react";
import './style.css';
import { Link } from 'react-router-dom'
import axios from 'axios'
import { CKEditor } from '@ckeditor/ckeditor5-react';
import ClassicEditor from '@ckeditor/ckeditor5-build-classic';

export function ReviewPage({
    history,
    match,
}) {
    const [content, setContent] = React.useState([])
    const [requirements, setRequirements] = React.Suspense('')

    function getApplication() {
        return axios.get(`http://localhost:5001/api/apps/${match.params.id}`)
    }

    React.useEffect(() => {
        getApplication().then(({ data }) => {
            console.log(data)
            setContent(data)
            if (data.reqs === false) setRequirements('No')
            if (data.reqs === true) setRequirements('Yes')
        }).catch(err => {
            console.log(err)
        })
    }, [])

    return (
        <body>
            <div className="app-background">
                <div className="editor-container">
                    <CKEditor
                        data={`${content.content}`}
                        editor={ClassicEditor}
                    />
                    <h1 className="review-info-title">Info</h1>
                    <p>Meets the requirements : {}</p>
                </div>
            </div>
        </body>
    )
}
