import './style.css'
import React from 'react'
import { getUserDetails } from '../../../utils/api'
import { Link } from '@material-ui/core'

export function NavLogin() {

    const [loggedIn, setLoggedIn] = React.useState(true)
    const [user, setUser] = React.useState({})
    const [open, setOpen] = React.useState(false)

    /*React.useEffect(() => {
        getUserDetails()
            .then(({ data }) => {
               setLoggedIn(true)
               setUser(data)
            }).catch(() => {
                setLoggedIn(false)
            })
    }, [])*/

    if (loggedIn) {
        return (
            <div>
                <div className={"navLogin " + (open ? "open" : "")} onClick={() => setOpen(!open)}>
                    <img className="navLogin-avatar" src="https://cdn.discordapp.com/avatars/359681648322805761/b5865a8fcbdfd01dc3017d7a137944a3.webp?size=128" alt="" />
                    <span className="navLogin-username">Zofux</span>
                    <i class="fas fa-caret-down"></i>
                </div>
                <div className={"menu " + (open ? "open" : "")}>
                    <p className="logout">Logout</p>
                </div>
            </div>
        )
    } else {
        return (
            <div>
                <p className="login" onClick={() => window.location.href = `https://simplify-code.com/api/auth/discord`}>Login</p>
            </div>
        )
    }
}
