export function DashboardPage(props) {
    const [count, setCount] = React.useState('0');

    const getNumberOfUsers = () => {
        axios.get('http://localhost:5001/api/users')
        .then((res) => {
            const data = res.data;
            setCount(data)
        })
        .catch(() => {
            console.log('There was an error getting the number of users.')
        })
    }
    getNumberOfUsers()

    const navSlide = () => {
        const burger = document.querySelector('.burger');
        const nav = document.querySelector('.nav-links')
        const navLinks = document.querySelectorAll('.nav-links a')

        // Toggle Links
        nav.classList.toggle('nav-active')


        //Animate Links
        navLinks.forEach((link, index) => {
            if (link.style.animation) {
                link.style.animation = ''
            } else {
                link.style.animation = `navLinkFade 0.5s ease forwards ${index / 5 + 0.5}s`
            }
        })

        //Burger Animation
        burger.classList.toggle('toggle')
    }

    return (
        <body>

            <div className="nav">

                <div className="page-cont">
                    <ul className="nav-links">
                        <Link><li><a className="aa" onClick={() => {
                            window.open("https://discord.com/invite/96MAcmwA", "_blank")
                        }}>Discord</a></li></Link>
                        <Link to="/rules"><li><a className="ab" >Rules</a></li></Link>
                        <Link to="/faq"><li><a className="ac" >FAQ</a></li></Link>
                        <Link to="/team"><li><a className="ad" >Team</a></li></Link>
                    </ul>

                    <div className="burger" onClick={navSlide}>
                        <div className="line1"></div>
                        <div className="line2"></div>
                        <div className="line3"></div>
                    </div>
                </div>

                <Link to="/">
                    <button className="login-btn">
                        Home
                        <a class="login-btn-logo"><i class="fas fa-home"></i></a>
                    </button>
                </Link>
            </div>

            <div className="info-container">

                <div className="info-box">
                    <h2>Total Users</h2>
                    <h1>
                        <CountUp end={count} />
                    </h1>
                </div>

                <div className="info-box">
                    <h2>Applications</h2>
                    <h1>
                        <CountUp end={8} />
                    </h1>
                </div>
            </div>

            <div className="app-container">
                <div className="app-box">
                    <h1>How to apply</h1>
                    <p>
                        If you want to become staff, support or any other role, then apply here. <br/>
                        Feel out the given questions and await a response from our bot <span className="discord-color-span">@Simplify Utilities#8736</span>.<br/>
                        If you wonder why we ask you specific questions read <Link to="/faq"><a>FAQ</a></Link> or message our <Link><a onClick={() => {window.open("https://discord.com/invite/96MAcmwA", "_blank")}}>ModMail</a></Link><br/>
                        <Link to="/applications">
                            <button className="discord_btn">
                            New.s<i class="fas fa-pencil-alt"></i>
                            </button>
                        </Link>
                    </p>
                </div>
            </div>
        </body>
    )
}
