import './style.css'
import React from 'react'
import { getUserDetails } from '../../../utils/api'
import { Link } from 'react-router-dom'
import axios from 'axios'

export function NavLoginDashboard() {

    const [loggedIn, setLoggedIn] = React.useState(false)
    const [user, setUser] = React.useState(null)
    const [open, setOpen] = React.useState(false)

    async function logout() {
        await axios.post("http://localhost:5001/api/logout", { id: user.discordId }).then(() => {
            setLoggedIn(false)
        })
    }

    React.useEffect(() => {
        getUserDetails()
            .then(({ data }) => {
               setLoggedIn(true)
               setUser(data)
            }).catch(() => {
                setLoggedIn(false)
            })
    }, [])

   /* if (loggedIn) {
        return (
            <div>
                <div className={"navLogin " + (open ? "open" : "")} onClick={() => setOpen(!open)}>
                    <img className="navLogin-avatar" src={user.avatar} alt="" />
                    <span className="navLogin-username">{user.username}</span>
                    <i class="fas fa-caret-down"></i>
                </div>
                <div className={"menu " + (open ? "open" : "")}>
                    <Link to="/"><p>Home</p></Link>
                    <Link to="/dashboard/report"><p>Report</p></Link>
                    <p className="logout" onClick={logout}>Logout</p>
                </div>
            </div>
        )
    } else {
        return (
                <button className="login" onClick={() => window.location.href = `http://localhost:5001/api/auth/discord`}>Login</button>
        )
    }*/
}
