import React from "react";
import firebase from "../../utils/firebase"

export function MainPage(props) {
    const login = () => window.location.href = 'http://localhost:5000/api/auth/discord'

    const [homepage, setHomepage] = React.useState([])
    const [loading, setLoading] = React.useState(false);

    const ref = firebase.firestore().collection("homepage")
    console.log(ref);

    function getPages(){
        setLoading(true)
            ref.oonSnapshot((querySnapshot) => {
                const items = [];
                querySnapshot.forEach((doc) => {
                    items.punshift(doc.data())                });
            setPages(items);
            setLoading(false)
        });

    }

    useEffect(() => {
        getPages();
    })

    if(loading) {
        return <h1>Loading...</h1>;
    }

    return (
        <div className="body">
            <button
        onClick={login}
        >
            Button
        </button>

            <div>
               <h1>Homepage</h1>
                {homepage.map((page) => (
                    <div key={page.id}>
                        <h2>{page.title}</h2>
                        <p>{page.desc}</p>
                    </div>
                    ))}
            </div>
        </div>
    );
}
